export interface CreateBackgroundData {
  /**
   * Height of the background visible area
   */
  height: string;
  /**
   * Width of the background visible area
   */
  width: string;
  /**
   * Angle under which are stripes created.
   */
  angle: string;
  /**
   * Width of the stripe
   */
  stripeWidth: string;
  /**
   * Color of first stripe
   */
  stripeOneColor: string;
  /**
   * Color of seconds stripe
   */
  stripeTwoColor: string;
}
